from flask_moment import Moment

moment = Moment(app)

@app.route('/mom/')
def mom():
    from datetime import datetime, timedelta
    current_time = datetime.utcnow() + timedelta(seconds=-60)
    return render_template('mom.html', current_time=current_time)


